$(function(){var n=$("#PlaceHolderHere");$('button[data-toggle="ajax-modal"]').click(function(){var t=$(this).data("url");$.get(t).done(function(t){n.html(t);n.find(".modal").modal("show")})});n.on("click",'[data-save="modal"]',function(){var t=$(this).parents(".modal").find("form"),i=t.attr("action"),r=t.serialize();$.post(i,r).done(function(){n.find(".modal").modal("hide")})})});